import axios from "axios";

const BASE_URL = "http://localhost:5000/api/";
const TOKEN = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYzMDE4ZDEzZTE5OTlmNDMzNjRlZmViMCIsImlzQWRtaW4iOnRydWUsImlhdCI6MTY2MTA0NzU3MSwiZXhwIjoxNjYzNjM5NTcxfQ.KkHQMoPN9nYKpHTAAnDo19u0vziJAPkz_YAiCmdKUVo";
//const TOKEN = JSON.parse(JSON.parse(localStorage.getItem("persist:root")).user).currentUser.accessToken;

export const publicRequest = axios.create({
  baseURL: BASE_URL,
});

export const userRequest = axios.create({
  baseURL: BASE_URL,
  headers: { token: `Bearer ${TOKEN}` },
});
